import React from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import {useEffect, useState} from 'react'
import axios from "axios";
const baseUrl='http://127.0.0.1:8000/api';
function TeacherDetail() {

  const [courseData, setcourseData]=useState([]);
  const [teacherData, setteacherData]=useState([]);
  let { teacher_id } = useParams();

  useEffect(()=>{
    try{
      axios.get(baseUrl+'/teacher/'+teacher_id).then((res)=>{
        setcourseData(res.data.teacher_courses);
        setteacherData(res.data);
      });
    }catch(error){
      console.log(error)
    }
  },[])



  return (
    <div className="container mt-3 ">
      <div className="row">
        <div className="col-4">
          <img src="/logo512.png" className="img-thumbnail" alt="Teacher-Img" />
        </div>
        <div className="col-8">
          <h3>{teacherData.fu}</h3>
          <p>
            Lorem Ipsum is simply dummy text of the printing and typesetting
            industry. Lorem Ipsum has been the industry's standard dummy text
            ever since the 1500s, when an unknown printer took a galley of type
            and scrambled it to make a type specimen book. Lorem Ipsum has been
            the industry's standard dummy text ever since the 1500s, when an
            unknown printer took a galley of type and scrambled it to make a
            type specimen book.
          </p>
          <p className="fw-bold">
            Skills: <Link to="/category/security">Security</Link>,{" "}
            <Link to="/category/security">Reactjs</Link>,{" "}
            <Link to="/category/security">Python</Link>,{" "}
            <Link to="/category/security">Django</Link>,
          </p>
          <p className="fw-bold">
            Recent Course: <Link to="/teacher-detail/1">IoT Security</Link>
          </p>
          <p className="fw-bold">Rating: 4/5</p>
        </div>
      </div>
      {/* Course Videos */}
      <div className="card mt-4">
        <h5 className="card-header">Course List</h5>
        <div className="list-group list-group-flush">
          <Link
            to="/detail/1"
            className="list-group-item list-group-item-action"
          >
            Cyberscurity Essentials 1
          </Link>
          <Link
            to="/detail/1"
            className="list-group-item list-group-item-action"
          >
            Cyberscurity Essentials 2
          </Link>
          <Link
            to="/detail/1"
            className="list-group-item list-group-item-action"
          >
            Network Security Essentials
          </Link>
          <Link
            to="/detail/1"
            className="list-group-item list-group-item-action"
          >
            Bash Scripting
          </Link>
          <Link
            to="/detail/1"
            className="list-group-item list-group-item-action"
          >
            Windows Privilege Escallation
          </Link>
          <Link
            to="/detail/1"
            className="list-group-item list-group-item-action"
          >
            IoT Security
          </Link>
        </div>
      </div>
    </div>
  );
}

export default TeacherDetail;
