import React from "react";
import {useEffect, useState} from 'react'
import axios from "axios";
import { useParams } from "react-router-dom";
const baseUrl='http://127.0.0.1:8000/api/public';
function AboutUs() {

    const [aboutus, setaboutus]=useState([]);



    useEffect(()=>{
        try{
            axios.get(baseUrl+'/about-us/').then((res)=>{
                setaboutus(res.data)
                console.log(res.data)
            })


          }catch(e){
            console.log(e)
          }
        },[])
    return(
        <div className="container mt-5">
          <div className="row">

            { aboutus.map((about, index) => (
                <div className="col-md-3 mb-4">
                  <div className="card w-100">
                    <Link to={`/course-detail/${about.id}`}>
                      <img
                        src={about.profile_img}
                        className="card-img-top"
                        width={200}
                        height={150}
                        alt={about.full_name}
                      />
                    </Link>
                    <hr />
                    <div className="card-body">
                      <h5 className="card-title">
                        <Link to={`/course-detail/${course.id}`}>{course.full_name}</Link>
                      </h5>
                      <p><b>Detail:</b>&nbsp;&nbsp; {course.details}...<Link to={`/course-detail/${course.id}`}>learn more</Link></p>
                    </div>
                    <div className="card-footer">
                      <div className="title">
                        <span className="float-end">{about.social}</span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
          </div>
        </div>
    )
}

export default AboutUs;
