import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import UserSidebar from "./UserSidebar";
import { useParams } from "react-router-dom";
import axios from "axios";

const baseUrl = 'http://127.0.0.1:8000/api/elearning'


function TakeQuiz() {
  const [questionData, setquestionData]=useState([]);
  const {quiz_id}=useParams();
  const studentId= localStorage.getItem('studentId')
  

  useEffect(() => {
    try{
      axios.get(baseUrl+'/quiz-questions/'+quiz_id+'/1').then((res)=>{
        setquestionData(res.data);
      });
    }catch(error){
      console.log(error)
    }
    document.title='Attempt Quiz';
  }, []);

  const submitAnswer=(question_id)=>{

  }

  return (
    <div className="container mt-4">
      <div className="row">
        <aside className="col-md-3"><UserSidebar /></aside>
        <section className="col-md-9">
          <h4 className="mb-3 border-bottom pb-1">Quiz Title</h4>
            {questionData.map((row, index)=>
              <div className="card">
                <h5 className="card-header">{row.questions}</h5>
                  <div className='card-body'>
                      <table className="table table-bordered">
                          <tbody>
                            <>
                              <tr>
                                <td>
                                  <button onClick={()=>submitAnswer(row.id)} className="btn btn-outline-secondary">{row.ans1}</button>
                                </td>
                              </tr>
                              <tr>
                                <td>
                                  <button onClick={()=>submitAnswer(row.id)} className="btn btn-outline-secondary">{row.ans2}</button>
                                </td>
                              </tr>
                              <tr>
                                <td>
                                  <button onClick={()=>submitAnswer(row.id)} className="btn btn-outline-secondary">{row.ans3}</button>
                                </td>
                              </tr>
                              <tr>
                                <td>
                                  <button onClick={()=>submitAnswer(row.id)} className="btn btn-outline-secondary">{row.ans4}</button>
                                </td>
                              </tr>
                            </>
                          </tbody>
                      </table>
                  </div>
              </div>
            )}
        </section>
      </div>
    </div>
  );
}
export default TakeQuiz;
