import React from "react";
import {useEffect, useState} from 'react'
import axios from "axios";
import { Link } from "react-router-dom";
const baseUrl='http://127.0.0.1:8000/api/public';
function AboutUs() {

    const [aboutus, setaboutus]=useState([]);



    useEffect(()=>{
        try{
            axios.get(baseUrl+'/about-us/').then((res)=>{
                setaboutus(res.data)
                console.log(res.data)
            })


          }catch(e){
            console.log(e)
          }
        },[])

    //Redication
    const SocialLinkedin = ()=>{
        {aboutus.map((about, index)=>(
            window.location.href=about.social_linkedin
        ))
        }
        }
    const SocialGithub = ()=>{
        {aboutus.map((about, index)=>(
            window.location.href=about.socialgithub
        ))
        }
        }
    return(
        <div className="container mt-5">
          <div className="row">

            { aboutus.map((about, index) => (
                <div className="col-md-3 mb-4">
                  <div className="card w-100">
                         <img
                        src={about.profile_img}
                        className="card-img-top"
                        width={200}
                        height={240}
                        alt={about.full_name}
                      />

                    <div className="card-body">
                      <h5 className="card-title">
                        <span className="fs-4">{about.full_name}</span>
                        <p className="fs-6">{about.designation}</p>
                      </h5>
                      <p>{about.details}</p>
                    </div>
                    <div className="card-footer">
                      <div className="title">
                        <Link onClick={SocialLinkedin} style={{textDecoration:'none', color:'black'}}><i className="bi bi-linkedin"></i></Link>
                        <Link onClick={SocialGithub} style={{textDecoration:'none',color:'black'}}><i class="bi bi-github"></i></Link>


                      </div>
                    </div>
                  </div>
                </div>
              ))}
          </div>
        </div>
    )
}

export default AboutUs;
