import React from 'react';
import {Link, useParams} from 'react-router-dom'
import Sidebar from './Sidebar';
import {useEffect, useState} from 'react'
import axios from 'axios';

const baseUrl = 'http://127.0.0.1:8000/api';

function UTD(){

    //Fetch training details
    const [trainingData, settrainingData] = useState([]);

    // Fetching Teacher data
    const teacherId = localStorage.getItem("teacherId");
    useEffect(() => {
        try {
        axios.get(baseUrl + "/training-details/"+teacherId).then((res) => {
            // console.log(res)
            settrainingData(res.data);
        });
        } catch (error) {
        console.log(error);
        }
    }, []);


    //Meeting redirection
    const RedMeeting = ()=>{
      {trainingData.map((meeting, index)=>(
        window.location.href=meeting.meeting_link
      ))
      }
  }

    return(
      <div className="container-fluid main_container">
      <div className="row">
        <aside className="col-md-3 ms-"><Sidebar /></aside>
        <div className="col-md-7 mt-3">

          <div className="card">
            <h5 className="card-header">Training Details</h5>

            <div className="card-body">
              <table className="table table-bordered">
                <tbody className='text-center'> 
                {trainingData.map((training, index) => (
                    <tr>
                        <td>
                            <Link to={`/course-detail/`+training.course.id}>{training.course.title}</Link>
                        </td>
                        <td>
                            {training.date}
                            </td>
                        <td>
                            {training.f_time}
                        </td>
                        <td>
                            {training.t_time}
                        </td>

                        <td>
                            <Link to={`/teacher-detail/`+training.teacher.id}>
                              {training.teacher.full_name}</Link>
                        </td>
                        <td>
                            <button target="_blank" className='btn btn-primary' onClick={RedMeeting}>Join meeting</button>
                        </td>
                    </tr>
                ))}    
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
    )
}

export default UTD;
