import React from "react";
import { Link } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";
import { useParams } from "react-router-dom";
import {useEffect, useState} from 'react'
import axios from "axios";
import Swal from 'sweetalert';

const baseUrl='http://127.0.0.1:8000/api';

function CourseChapter() {
    const [chapterData, setchapterData]=useState([]);
    const [totalResult, settotalResult]=useState([]);
    const {course_id}=useParams();

    // console.log(teacherId);

    useEffect(()=>{
      try{
        axios.get(baseUrl+'/course-chapters/'+course_id).then((res)=>{
          setchapterData(res.data)
          settotalResult(res.data.length)
        });
      }catch(error){
        console.log(error)
      }
    },[])

    const Swal = require('sweetalert2')
    const handleDelete =()=>{
        Swal.f
    }
    return(
        <div className="container mt-4">
      <div className="row">
        <aside className="col-md-3">
          <TeacherSidebar />
        </aside>
          <div className="col-md-9">
          <div className="card">
            <h5 className="card-header">All Chapters ({totalResult})</h5>
            <div className="card-body">
            <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Vidoe</th>
                    <th>Remarks</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {chapterData.map((chapter, index)=>
                  <tr>
                    <td><Link to={`/edit-chapter/`}>{chapter.title}</Link></td>
                    <td>
                        <video controls width="250">
                            <source src={chapter.video.url} type="video/webm" />
                            <source src={chapter.video.url} type="video/mp4" />
                            Sorry your browser dosen't support emedded video.
                        </video>

                    </td>
                    <td>{chapter.remarks}</td>
                    <td>
                        <Link to={`/edit-chapter/`} className="btn btn-info btn-sm  px-4 text-white active"><i className="bi bi-pencil-square"></i></Link>
                        <Link to={`/delete-chapter/`} className="btn btn-danger btn-sm ms-2 text-white px-4 active"><i className="bi bi-trash"></i></Link>
                    </td>
                  </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
          </div>
      </div>
    </div>
    )
}
export default CourseChapter;
