import React from "react";
import { Link, useParams} from "react-router-dom";
import { useEffect, useState } from "react";
import axios from 'axios';
const baseUrl='http://127.0.0.1:8000/api';


function LearningPage(){

    const [chapterData, setchapterData]=useState([]);
    let {course_id}=useParams();

    useEffect(()=>{
        try{
            axios.get(baseUrl+'/course-chapters/'+course_id).then((res)=>{
                setchapterData(res.data)
            })
        }catch(error){
            console.log(error);
        }
    }, []);

    // Video
    const VideoFile = ()=>{
        {chapterData.map((chapter, index)=>(
          window.location=chapter.video
        ))
        }
    }

    return(
        <div className="container-fluid main_container mt-3">
            <div className='row'>
                <div className='col-md-3'>
                    <p className="fs-4"><strong>Chapters</strong></p>
                    <hr />
                    { chapterData.map((row, index) => (
                        <>
                        <div>
                            <Link onClick={VideoFile}>{row.title}</Link>
                        </div>
                        <hr />
                        </>
                    ))}
                </div>
                <div className='col-md-8'>
                    <div>
                        {/* <video controls width="500">
                            <source src={chapterData.video} type="video/mp4" />
                            Sorry your browser dosen't support emedded video.
                        </video> */}

                    </div>
                    <div>{chapterData.description}</div>
                </div>
            </div>
        </div>
    )
}

export default LearningPage;
