import React from "react";
import TeacherSidebar from "./TeacherSidebar";
import {useEffect, useState} from 'react';
import axios from "axios";

const baseUrl='http://127.0.0.1:8000/api';

function AddCourse() {

  useEffect(()=>{
    document.title="Add Course"
  });

  const [cats, setCates]=useState([]);
  const [couseData, setCouseData]=useState([]);
  useEffect(()=>{
    try{
      axios.get(baseUrl+'/category/').then((res)=>{
              setCates(res.data)
      });
    }catch(error){
      console.log(error)
    }
  },[])

  const handleChange=(event)=>{
    setCouseData({
        ...couseData,
        [event.target.name]:event.target.value
    });
}

  const handleFileChange=(event)=>{
    setCouseData({
        ...couseData,
        [event.target.name]:event.target.files[0]
    });
}


const submitForm=()=>{
  const _FormData=new FormData();
  _FormData.append("category", couseData.category);
  _FormData.append("teacher", 1);
  _FormData.append("title", couseData.title);
  _FormData.append("description", couseData.description);
  _FormData.append("featured_img", couseData.featured_img);
  _FormData.append("technologies", couseData.technologies);

  try{
    axios.post(baseUrl+'/course/', _FormData,{
      headers: {
        'content-type': 'multipart/form-data'
      }
    })
    .then((res)=>{
      console.log(res.data);
    })
  }catch(error){
    console.log
  }

};
  // console.log(cats)

  return (
    <div className="container mt-4">
      <div className="row">
        <aside className="col-md-3">
          <TeacherSidebar />
        </aside>
        <section className="col-md-9">  
            <div className="card">
                <h5 className="card-header">Add Course</h5>
                <div className="card-body">
                    <div className="mb-3">
                        <label for="title" className="form-label">Category</label>
                        <select name="category" className="form-control">
                          {cats.map((category,index)=>{return<option key={index}>{category.title}</option>})}
                        </select>
                    </div>
                    <div className="mb-3">
                        <label for="title" className="form-label">Title</label>
                        <div className="col-sm-10">
                            <input type="text" className="form-control" id="staticEmail" />
                        </div>
                    </div>
                    <div className="mb-3">
                        <label for="description" className="form-label">Description</label>
                        <div className="col-sm-10">
                        <textarea className='form-control'></textarea>
                        </div>
                    </div>
                    <div className="mb-3">
                        <label for="video" className="form-label">Course Videos</label>
                        <div className="col-sm-10">
                            <input type="file" className="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div className='mb-3'>
                        <label for="technology" className='form-label'>Technologies</label>
                        <textarea className='form-control'></textarea>
                    </div>
                    <button type='submit' className='btn btn-primary'>Add</button>
                </div>
            </div> 
        </section>
      </div>
    </div>
  );
}

export default AddCourse;
