import React from "react";
import TeacherSidebar from "./TeacherSidebar";
import { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
const baseUrl = "http://127.0.0.1:8000/api";

function EditChapter() {
    const [ChapterData, setChapterData] = useState({
        course: "",
        title: "",
        description: "",
        video: "",
        remarks: "",
    });



    const handleChange = (event) => {
        setChapterData({
            ...ChapterData,
            [event.target.name]: event.target.value,
        });
    };

    const handleFileChange = (event) => {
        setChapterData({
            ...ChapterData,
            [event.target.name]: event.target.files[0],
        });
    };

    const { chapter_id } = useParams();
    const submitForm = () => {
        const _FormData = new FormData();
        _FormData.append("course", chapter_id);
        _FormData.append("title", ChapterData.title);
        _FormData.append("description", ChapterData.description);
        _FormData.append("video", ChapterData.video);
        _FormData.append("remarks", ChapterData.remarks);

        try {
            axios
                .post(baseUrl + "/chapter/", _FormData, {
                    headers: {
                        "content-type": "multipart/form-data",
                    },
                })
                .then((res) => {
                    // console.log(res.data);
                    window.location.href = "/all-chapter";
                });
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(()=>{
        try{
          axios.get(baseUrl+'/chapter/'+chapter_id).then((res)=>{
            setchapterData(res.data)
          });
        }catch(error){
          console.log(error)
        }
      },[])

    return (
        <div className="container mt-4">
            <div className="row">
                <aside className="col-md-3">
                    <TeacherSidebar />
                </aside>
                <section className="col-md-9">
                    <div className="card">
                        <h5 className="card-header">Update Chapter</h5>
                        <div className="card-body">
                            <div className="mb-3">
                                <label for="title" className="form-label">
                                    Title
                                </label>
                                <input value={chapter}
                                    onChange={handleChange}
                                    name="title"
                                    type="text"
                                    id="title"
                                    className="form-control"
                                />
                            </div>
                            <div className="mb-3">
                                <label for="description" className="form-label">
                                    Description
                                </label>
                                <div className="col-sm-10">
                                    <textarea
                                        onChange={handleChange}
                                        name="description"
                                        className="form-control"
                                    ></textarea>
                                </div>
                            </div>
                            <div className="mb-3">
                                <label for="video" className="form-label">
                                    Video
                                </label>
                                <input
                                    onChange={handleFileChange}
                                    name="video"
                                    type="file"
                                    className="form-control"
                                />
                                <div className="col-sm-10"></div>
                            </div>
                            <div className="mb-3">
                                <label for="remark" className="form-label">
                                    Remarks
                                </label>
                                <input
                                    onChange={handleChange}
                                    name="remarks"
                                    type="text"
                                    id="remark"
                                    className="form-control"
                                    placeholder="This video is focused on basic introduction"
                                />
                            </div>
                            <button
                                type="button"
                                onClick={submitForm}
                                className="btn btn-primary">
                                Update
                            </button>
                        </div>
                    </div>
                </section>
            </div>
        </div>
    );
}

export default EditChapter;
