import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import UserSidebar from "./UserSidebar";
import axios from "axios";
import Swal from 'sweetalert2'

const baseUrl = 'http://127.0.0.1:8000/api'

function FavoriteCourses() {

  const [courseData, setcourseData]=useState([]);
  const studentId= localStorage.getItem('studentId')
  const [favoriteStatus, setfavoriteStatus]=useState();
  let {course_id}=useParams();

  useEffect(() => {
    try {
      axios.get(baseUrl + "/fetch-favorite-courses/"+studentId).then((res) => {
        setcourseData(res.data);
      });
    } catch (error) {
      console.log(error);
    }
  }, []);

  const removeFavorite=()=>{
    const _formData=new FormData();
    _formData.append('course', course_id);
    _formData.append('student', studentId);
    _formData.append('status',true);

    try{
      axios.get(baseUrl+'/student-remove-favorite-course/'+course_id+'/'+studentId,{
        headers:{
          'content-type': 'multipart/form-data'
        }
      }).then((res)=>{
        if (res.status===200 || res.status===201){
          Swal.fire({
            title:'Removed From favorite',
            icon:'success',
            toast:true,
            timer:10000,
            position:"top-right",
            timerProgressBar:true,
            showCancelButton:false
          });
          setfavoriteStatus('');
        }
      })
    }catch(e){
      console.log(e);
    }
  };

  return (
    <div className="container-fluid main_container">
      <div className="row">
        <aside className="col-md-3"><UserSidebar /></aside>
        <div className="col-md-7 mt-3">
        <h5 className="pb-1 mb-4 fs-4">Favorite Courses</h5>
          <div className='row'>
            {courseData.map((row,index)=>
            <div className='col-4 mb-4'>
            <div className="card">
              <Link to={`/course-detail`}>
                <img src={row.course.featured_img} className="card-img-top" width={200} height={150} alt={row.course.title} />
              </Link>
              <hr/>
              <div className="card-body">
                <h5 className="card-title">
                <p>Course: <Link to={`/course-detail/`+row.course.id}>{row.course.title}</Link></p>
                </h5>
                <p>Trainer:<Link to={`/teacher-detail/`+row.course.teacher.id}>{row.course.teacher.full_name}</Link></p>
                <p>Description:{row.course.description}<Link to={`/course-detail/`+row.course.id}>..more</Link></p>
                <hr/>
                <button className='btn btn-ligth ms-5 px-3 text-light' ><Link to={`/course-detail/`+row.course.id} style={{ textDecoration: 'none' }}>Start</Link></button>
                <button type="button" className='btn btn-danger ms-5 px-3' onClick={removeFavorite} >Remove</button>
              </div>
            </div>
          </div>
          )}
          </div>
        </div>
      </div>
    </div>
  );
}
export default FavoriteCourses;
