import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Sidebar from "./Sidebar";
import axios from "axios";

const baseUrl = 'http://127.0.0.1:8000/api'

function RecommendedCourses() {
  const [courseData, setcourseData]=useState([]);
  const studentId =localStorage.getItem('studentId');

  //Fetching courses enrolled by student
  useEffect(()=>{
    try{
      axios.get(baseUrl+'/fetch-enrolled-courses/'+studentId).then((res)=>{
        setcourseData(res.data);
      })
    }catch(e){
      console.log(e);
    }
  })
  return (
    <div className="container-fluid main_container">
      <div className="row">
        <aside className="col-md-3">
          <Sidebar />
        </aside>
          <div className="col-md-7 mt-3">
          <div className="card">
            <h5 className="card-header">Recommended Courses</h5>
            <div className="card-body">
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>Name</th> <th>Technologies</th>
                  </tr>
                </thead>
                <tbody>
                  {courseData.map((row, index)=>
                  <>
                  <td><Link to={`/course-detail/`+row.course.id}>{row.course.title}</Link></td>
                  <td>{row.course.technologies}</td>
                  </>
                  )}
                </tbody>
              </table>
            </div>
          </div>
          </div>
      </div>
    </div>
  );
}
export default RecommendedCourses;
