import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from 'axios';
const baseUrl='http://127.0.0.1:8000/api/elearning';

function AllCourses(){

    const [courseData, setCourseData]=useState([]);
    


    useEffect(()=>{
        try{
            axios.get(baseUrl+'/courses/').then((res)=>{
                setCourseData(res.data.results)
            })
        }catch(error){
            console.log(error);
        }
    }, []);
    return(
        <div className="container-fluid main_container mt-3">
             <div className="row">
                <div className="col-3">
                    course category
                </div>
            {/* Latest Course */}
            <div className="col-8">
            <h3 className="pb-1 mb-4">All Courses</h3>
            <div className="row">
            
            
            { courseData.map((course, index) => (
              <>
              {course.teacher.id !==2 &&
                <div className="col-md-3 mb-4">
                  <div className="card w-100">
                    <Link to={`/course-detail/${course.id}`}>
                      <img
                        src={course.featured_img}
                        className="card-img-top"
                        width={200}
                        height={150}
                        alt={course.title}
                      />
                    </Link>
                    <hr />
                    <div className="card-body">
                      <h5 className="card-title">
                        <Link to={`/course-detail/${course.id}`}>{course.title}</Link>
                      </h5>
                      <p><b>Detail:</b>&nbsp;&nbsp; {course.description}...<Link to={`/course-detail/${course.id}`}>learn more</Link></p>
                    </div>
                    <div className="card-footer">
                      <div className="title">
                        <span><b>Rating:</b>&nbsp;&nbsp;{course.course_rating}/5</span>
                        <span className="float-end"><b>Enrolled:&nbsp;&nbsp;{course.total_enrolled_students}</b></span>
                      </div>
                    </div>
                  </div>
                </div>
              }
              </>
              ))}
          </div>
            
            {/* End Latest Courses */}
            {/* pagination Start */}
            <nav aria-label="Page navigation example mt-5">
                <ul className="pagination justify-content-center">
                    <li className="page-item"><button className="page-link"><i class="bi bi-arrow-left"></i>Previous</button></li>
                    <li className="page-item"><button className="page-link">Next<i class="bi bi-arrow-right"></i></button></li>
                </ul>
            </nav>
            {/* pagination End */}
            </div>
        </div>
        </div>
    )
}

export default AllCourses;
