import React from "react";
import TeacherSidebar from "./TeacherSidebar";
import {useEffect, useState} from 'react'
import axios from "axios"

const baseUrl='http://127.0.0.1:8000/api'

function AddCourse() {

  useEffect(()=>{
    document.title="Add Course"
  });

  const [cats, setCates]=useState([]);
  useEffect(()=>{
    try{
      axios.get(baseUrl+'/category/').then((res)=>{
          if(res.data.bool===true){
              setCates({})
          }
      });
    }catch(error){
      console.log(error)
    }
  },[])


  return (
    <div className="container mt-4">
      <div className="row">
        <aside className="col-md-3">
          <TeacherSidebar />
        </aside>
        <section className="col-md-9">  
            <div className="card">
                <h5 className="card-header">Add Course</h5>
                <div className="card-body">
                    <div className="mb-3 row">
                        <label for="title" className="form-label">Category</label>
                        <select name="category" className="form-control"></select>
                    </div>
                    <div className="mb-3 row">
                        <label for="title" className="form-label">Title</label>
                        <div className="col-sm-10">
                            <input type="text" className="form-control" id="staticEmail" />
                        </div>
                    </div>
                    <div className="mb-3 row">
                        <label for="description" className="form-label">Description</label>
                        <div className="col-sm-10">
                        <textarea className='form-control'></textarea>
                        </div>
                    </div>
                    <div className="mb-3 row">
                        <label for="video" className="form-label">Course Videos</label>
                        <div className="col-sm-10">
                            <input type="file" className="form-control" id="inputPassword" />
                        </div>
                    </div>
                    <div className='mb-3'>
                        <label for="technology" className='form-label'>Technologies</label>
                        <textarea className='form-control'></textarea>
                    </div>
                    <button type='submit' className='btn btn-primary'>Add</button>
                </div>
            </div> 
        </section>
      </div>
    </div>
  );
}

export default AddCourse;
