import React from 'react';
import {useEffect, useState} from 'react';
import axios from 'axios';

const baseUrl='http://127.0.0.1:8000/api';

function TeacherLogin(){
    const [teacherLoginData, setTeacherLoginData] = useState({
        'email':'',
        'password':'',
    });

    const handleChange=(event)=>{
        setTeacherLoginData({
            ...teacherLoginData,
            [event.target.name]:event.target.value
        });
    }

    const submitForm = ()=>{
        console.log(teacherLoginData);
        const teacherFormData=new FormData;
        teacherFormData.append('email', teacherLoginData.email)
        teacherFormData.append('password', teacherLoginData.password)
        try{
            axios.post(baseUrl+'/teacher-login', teacherFormData).then((res)=>{
                if(res.data.bool==true){
                    localStorage.setItem('teacherLoginStatus',true)
                }
            });
        }catch(error){
            console.log(error)
        }
    }


    useEffect(()=>{
        document.title="Teacher Login"
    });

    return(
        <div className='container mt-4'>
            <div className='row'>
                <div className='col-6 offset-3'>
                    <div className='card'>
                        <h5 className='card-header'>Trainer Login</h5>
                        <div className='card-body'>
                            <form>
                                <div className='mb-3'>
                                    <label for="exampleInputEmail1" className='form-label'>Email</label>
                                    <input alue={teacherLoginData.email} onChange={handleChange} name="email" type="email" className="form-control" />
                                </div>
                                <div className='mb-3'>
                                    <label for="exampleInputPassword1" className='form-label'>Password</label>
                                    <input alue={teacherLoginData.password} onChange={handleChange} name="password" type="password" className="form-control" id="exampleInputPassword1" />
                                </div>
                                <button  type="button" onClick={submitForm} className='btn btn-primary'>Login</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>  
        </div>
    )
}

export default TeacherLogin;
