import React from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import {useEffect, useState} from 'react'
import axios from "axios";
const baseUrl='http://127.0.0.1:8000/api';
const siteUrl='http://127.0.0.1:8000/';



function CourseDetail() {
  const [chapterData, setchapterData]=useState([]);
  const [courseData, setcourseData]=useState([]);
  const [teacherData, setteacherData]=useState([]);
  const [relatedCourseData, setrelatedCourseData]=useState([]);
  const [techListData, settechListData]=useState([]);
  let { course_id } = useParams();

  useEffect(()=>{
    try{
      axios.get(baseUrl+'/course/'+course_id).then((res)=>{
        setcourseData(res.data);
        setchapterData(res.data.course_chapter);
        setteacherData(res.data.teacher);
        setrelatedCourseData(JSON.parse(res.data.related_videos));
        settechListData(JSON.parse(res.data.tech_list))
      });
    }catch(error){
      console.log(error)
    }
  },[])

  // console.log(relatedCourseData);
  return (
    <div className="container mt-3 ">
      <div className="row">
        <div className="col-4">
          <img src={courseData.featured_img} className="img-thumbnail float-end" width={350}  alt={chapterData.title} />
        </div>
        <div className="col-8">
          <h3>{courseData.title}</h3>
          <p>{courseData.description}</p>
          <p className="fw-bold">
            Course by: <Link to={`/teacher-detail/${teacherData.id}`}>{teacherData.full_name}</Link>
          </p>
          <p className="fw-bold">
            Technologies: 
            {techListData.map((tech, index)=>
             <Link to={"/"}>{tech}</Link>
            )}
          </p>
          <p className="fw-bold">Duration: 3 hours 30 Minutes</p>
          <p className="fw-bold">Total Enrolled: 456 Students</p>
          <p className="fw-bold">Rating: 4/5</p>
        </div>
      </div>

      {/* Course Videos */}
      <div className="card mt-4">
        <h5 className="card-header">In this course</h5>
        <ul className="list-group list-group-flush">
          {chapterData.map((chapter, index)=>
          <li className="list-group-item" value={chapter.id}>
            {chapter.title}
            <span className="float-end">
              <span className="me-5">1 Hour 30 Minutes</span>
              <button
                className="btn btn-sm btn-danger"
                data-bs-toggle="modal"
                data-bs-target="#VideoModal"
              >
                <i className="bi-youtube"></i>
              </button>
            </span>
            {/* Video Modal start */}
            <div
              className="modal fade"
              id="VideoModal"
              tabindex="-1"
              aria-labelledby="exampleModalLabel"
              aria-hidden="true"
            >
              <div className="modal-dialog modal-xl">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title" id="exampleModalLabel">
                      BS:Video 1
                    </h5>
                    <button
                      type="button"
                      className="btn-close"
                      data-bs-dismiss="modal"
                      aria-label="Close"
                    ></button>
                  </div>
                  <div className="modal-body">
                    <div class="ratio ratio-16x9">
                      <iframe
                        src={chapter.video}
                        title="YouTube video"
                        allowfullscreen
                      ></iframe>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            {/* Video Modal End */}
          </li>
          )}
        </ul>
      </div>

      <h3 className="pb-1 mb-4 mt-5">Related Course</h3>
      <div className="row mb-4">
        {relatedCourseData.map((rcourse, index)=>
          <div className="col-md-3">
            <div className="card">
              <Link target="_blank" to={`/detail/${rcourse.pk}`}>
                <img src={`${siteUrl}media/${rcourse.fields.featured_img}`} className="card-img-top" alt="Img" />
              </Link>
              <div className="card-body">
                <h5 className="card-title">
                  <Link target="_blank" to={`/detail/${rcourse.pk}`}>{rcourse.fields.title}</Link>
                </h5>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CourseDetail;
