import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import UserSidebar from "./UserSidebar";
import axios from "axios";

const baseUrl = 'http://127.0.0.1:8000/api/elearning'


function StudentAssignments() {
  const [assignmentData, setassignmentData]=useState([]);
  const [assignmentStatus, setassignmentStatus]=useState('');
  const studentId= localStorage.getItem('studentId')
  

  useEffect(() => {
    try {
      axios.get(baseUrl + "/my-assignments/"+studentId).then((res) => {
        setassignmentData(res.data);
      });
    } catch (error) {
      console.log(error);
    }
  }, []);


  const markAsDone=(assignment_id)=>{
    const _FormData = new FormData();
    _FormData.append("status", true);

    try {
      axios
        .put(baseUrl + "/update-assignment/"+assignment_id, _FormData, {
          headers: {
            "content-type": "multipart/form-data",
 
          },
        }).then((res) => {
          if(res.status===200 || res.status===201){
            Swal.fire({
              title: "You have successsfully completed this assignment",
              icon: "success",
              toast:"true",
              timer:"1500",
              position:"top-right",
              timerProgressBar:true,
              showConfirmButton:false
            });
            assignmentStatus('success')
          }  
          
        });
    } catch (error) {
      console.log(error);
    }
  };


  return (
    <div className="container mt-4">
      <div className="row">
        <aside className="col-md-3"><UserSidebar /></aside>
        <section className="col-md-9">
            <div className="card">
                <h5 className="card-header">My Assignments</h5>
                <div className='card-body'>
                    <table className="table table-bordered">
                        <thead>
                            <tr>
                                <th>Title</th>
                                <th>Detail</th>
                                <th>Teacher</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {assignmentData.map((row,index)=>
                            <tr>
                                <td>{row.title}</td>
                                <td>{row.detail}</td>
                                <td><Link to={`/teacher-detail/`+row.teacher.id}>{row.teacher.full_name}</Link></td>
                                <td>
                                    {assignmentStatus!=='success' &&
                                        <button onClick={markAsDone} className="btn btn-success">Mark as Done</button>
                                    }
                                </td>
                            </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>
        </section>
      </div>
    </div>
  );
}
export default StudentAssignments;
