import React from "react";
import TeacherSidebar from "./TeacherSidebar";
import {useEffect, useState} from 'react';
import axios from "axios";

const baseUrl='http://127.0.0.1:8000/api';

function AddChapter() {

  useEffect(()=>{
    document.title="Add Chapter"
  });

  const [ChapterData, setChapterData]=useState({
    'title':'',
    'description':'',
    'featured_img':'',
    'technologies':'',
  });

  useEffect(()=>{
    try{
      axios.get(baseUrl+'/category').then((res)=>{
              setCates(res.data)
      });
    }catch(error){
      console.log(error)
    }
  },[])

  const handleChange=(event)=>{
    setChapterData({
        ...ChapterData,
        [event.target.name]:event.target.value
    });
}

  const handleFileChange=(event)=>{
    setChapterData({
        ...ChapterData,
        [event.target.name]:event.target.files[0]
    });
}


const submitForm=()=>{
  const _FormData=new FormData();
  _FormData.append("category",1); //ChapterData.category
  _FormData.append("teacher", 1);
  _FormData.append("title", ChapterData.title);
  _FormData.append("description", ChapterData.description);
  _FormData.append("featured_img", ChapterData.featured_img);
  _FormData.append("technologies", ChapterData.technologies);

  try{
    axios.post(baseUrl+'/course', _FormData,{
      headers: {
        'content-type': 'multipart/form-data'
      }
    })
    .then((res)=>{
      // console.log(res.data);
      window.location.href='/add-course';
    })
  }catch(error){
    console.log(error);
  }

};
  // console.log(cats)


  return (
    <div className="container mt-4">
      <div className="row">
        <aside className="col-md-3">
          <TeacherSidebar />
        </aside>
        <section className="col-md-9">  
            <div className="card">
                <h5 className="card-header">Add Chapter</h5>
                <div className="card-body"> 
                    <div className="mb-3">
                        <label for="title" className="form-label">Title</label>
                        <input type="text" id="title" className="form-control" />
                    </div>
                    <div className="mb-3">
                        <label for="description" className="form-label">Description</label>
                        <div className="col-sm-10">
                        <textarea   name="description" className='form-control'></textarea>
                        </div>
                    </div>
                    <div className="mb-3">
                        <label for="video" className="form-label">Video</label>
                        <input type="file" className='form-control' />
                        <div className="col-sm-10">

                        </div>
                    </div>
                    <div className="mb-3">
                        <label for="remark" className="form-label">Remark</label>
                        <input type="text" id="remark" className="form-control" placeholder="This video is focused on basic introduction" />
                    </div>
                    <button type='button' className='btn btn-primary'>Add</button>
                </div>
            </div> 
        </section>
      </div>
    </div>
  );
}

export default AddChapter;
