import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from 'axios';
const baseUrl='http://127.0.0.1:8000/api';

function AllCourses(){

    const [courseData, setCourseData]=useState([]);

    useEffect(()=>{
        try{
            axios.get(baseUrl+'/course/').then((res)=>{
                setCourseData(res.data)
            })
        }catch(error){
            console.log(error);
        }
    }, []);
    return(
        <div className="container-fluid main_container mt-3">
             <div className="row">
                <div className="col-2">

                </div>
            {/* Latest Course */}

            <h3 className="pb-1 mb-4">All Courses</h3>
            <div className="row">
                {courseData && courseData.map((course, index)=>
                <div className="col-3">
                    <div className="card mb-4" >
                        <Link to={`/course-detail/${course.id}`}><img src={course.featured_img} className="card-img-top" width={300} height={300} alt={course.title} /></Link>
                        <div className="card-body">
                            <h5 className="card-title"><Link to={`/course-detail/${course.id}`}>{course.title}</Link></h5>
                        </div>
                    </div>
                </div>
                )}
            </div>
            {/* End Latest Courses */}
            {/* pagination Start */}
            <nav aria-label="Page navigation example mt-5">
                <ul className="pagination justify-content-center">
                    <li className="page-item"><a className="page-link" href="/#">Previous</a></li>
                    <li className="page-item"><a className="page-link" href="/#">1</a></li>
                    <li className="page-item"><a className="page-link" href="/#">2</a></li>
                    <li className="page-item"><a className="page-link" href="/#">3</a></li>
                    <li className="page-item"><a className="page-link" href="/#">Next</a></li>
                </ul>
            </nav>
            {/* pagination End */}
        </div>
        </div>
    )
}

export default AllCourses;
